// Test registration response
pm.test("Status code is 200", function () {
    pm.response.to.have.status(200);
});

pm.test("Response has success property", function () {
    var jsonData = pm.response.json();
    pm.expect(jsonData).to.have.property('success');
    pm.expect(jsonData.success).to.be.true;
});

// Save test user credentials for cleanup
pm.environment.set("test_user_username", "testuser");
pm.environment.set("test_user_password", "TestPassword123!");

// Test login response
pm.test("Status code is 200", function () {
    pm.response.to.have.status(200);
});

pm.test("Response has token", function () {
    var jsonData = pm.response.json();
    pm.expect(jsonData).to.have.property('token');
    pm.expect(jsonData.token).to.be.a('string');
    pm.expect(jsonData.token).to.not.be.empty;
});

pm.test("Response has expiration", function () {
    var jsonData = pm.response.json();
    pm.expect(jsonData).to.have.property('expiration');
    pm.expect(new Date(jsonData.expiration)).to.be.above(new Date());
});

pm.test("Response has username and role", function () {
    var jsonData = pm.response.json();
    pm.expect(jsonData).to.have.property('username', 'admin');
    pm.expect(jsonData).to.have.property('role', 'Admin');
});

// Save admin token for subsequent requests
pm.environment.set("admin_token", jsonData.token);
pm.environment.set("admin_username", jsonData.username);

// Test login response
